// immutable classpublic class StockPurchase{	private int shares;	private double cost;		public StockPurchase(int numberOfShares, double costPerShare)	{		shares = numberOfShares;		cost = costPerShare;	} // end constructor		public int getNumberOfShares()	{		return shares;	} // end getNumberOfShares		public double getCostPerShare()	{		return cost;	} // end getCostPerShare		public double getValue() // don't need	{		return shares * cost;	} // end getValue} // end StockPurchase