// The class Name overrides hashCode in a way that causes collisions for this test.import java.io.*;import java.util.*;public class Driver{	public static void main(String[] args) 	{		testDictionary();		testHashTable();		System.out.println("\n\nDone.");	}  // end main		public static void testDictionary()	{// create a dictionary using a hash table whose initial size is 11//		DictionaryInterface nameList = new HashedMapOpenAddressing(11);		HashedMapOpenAddressing nameList = new HashedMapOpenAddressing(11); // enables display method		System.out.println("Create a dictionary whose initial hash table has 11 locations:\n");		System.out.println("Initial dictionary should be empty; isEmpty() returns " + nameList.isEmpty());		// test add				System.out.println("\n\nTesting add() - add 5 names:\n");		nameList.add(new Name("Tabatha"), "555-1234");		nameList.add(new Name("Abel"), "555-5678");		nameList.add(new Name("Sam"), "555-9012");		nameList.add(new Name("Dirk"), "555-3456");   		nameList.add(new Name("Miguel"), "555-7890");				System.out.println("Dictionary should not be full; isFull() returns " + nameList.isFull() + "\n");		System.out.println("Dictionary contains " + nameList.getSize() + " (should be 5) items, as follows");		System.out.println("  (notice that it is not sorted):\n");			display(nameList);			System.out.println("\nThe hash table is:\n");		nameList.display(); // display hash table [METHOD ADDED TO CLASS FOR TESTING PURPOSES]		System.out.println("\n\nAdd 6 more names (causes rehashing):\n");		nameList.add(new Name("Tom"), "555-5555");		nameList.add(new Name("Reiss"), "555-2345");		nameList.add(new Name("Bette"), "555-7891");		nameList.add(new Name("Carole"), "555-7892");		nameList.add(new Name("Derek"), "555-7893");		nameList.add(new Name("Nancy"), "555-7894");		System.out.println("Dictionary should not be full; isFull() returns " + nameList.isFull() + "\n");		System.out.println("Dictionary contains " + nameList.getSize() + " (should be 11) items, as follows:\n");		display(nameList);		System.out.println("\nThe hash table is:\n");		nameList.display(); // display hash table		// test getValue				System.out.println("\n\nTesting getValue():\n");		System.out.println("\nAbel:   " + nameList.getValue(new Name("Abel")));		System.out.println("\nSam:    " + nameList.getValue(new Name("Sam")));		System.out.println("\nTom:    " + nameList.getValue(new Name("Tom")));		System.out.println("\nReiss:  " + nameList.getValue(new Name("Reiss")));		System.out.println("\nMiguel: " + nameList.getValue(new Name("Miguel")));	// test contains		System.out.println("\n\n\nTesting contains():\n");		if ( nameList.contains(new Name("Sam")) )			System.out.println("\nSam is in dictionary - OK");		else 			System.out.println("Error in contains()");				if ( nameList.contains(new Name("Abel")) )			System.out.println("\nAbel is in dictionary - OK");		else 			System.out.println("Error in contains()");				if ( nameList.contains(new Name("Miguel")) )			System.out.println("\nMiguel is in dictionary - OK");		else 			System.out.println("Error in contains()");				if ( nameList.contains(new Name("Tom")) )			System.out.println("\nTom is in dictionary - OK");		else 			System.out.println("Error in contains()");		if (!nameList.contains(new Name("Dave")))			System.out.println("\nDave is not in dictionary - OK");		else 			System.out.println("Error in contains()");// remove 		System.out.println("\n\n\nRemoving Abel - Abel's number is " + nameList.remove(new Name("Abel")));// test replacing value		System.out.println("Replacing phone number of Reiss and Miguel:\n");		nameList.add(new Name("Reiss"), "555-5432");		nameList.add(new Name("Miguel"), "555-9120");     		System.out.println("Dictionary contains " + nameList.getSize() + " (should be 10) items, as follows:\n");		display(nameList);			// remove 		System.out.println("\n\nRemoving Dirk and Tom:\n");		nameList.remove(new Name("Dirk"));		nameList.remove(new Name("Tom"));		System.out.println("Dictionary contains " + nameList.getSize() + " (should be 8) items, as follows:\n");		display(nameList);		System.out.println("\nThe hash table is:\n");		nameList.display(); // display hash table// test clear		System.out.println("\n\nTesting clear():\n");		nameList.clear();				System.out.println("Dictionary should be empty; isEmpty() returns " + nameList.isEmpty());		} // testDictionary		/** Tests the hash table when no locations contain null */	public static void testHashTable()	{		HashedMapOpenAddressing nameList = new HashedMapOpenAddressing(11); // enables display method		System.out.println("\n\n-----------------------------------------------------------------------\n");		System.out.println("testHashTable():");		System.out.println("Create a dictionary whose initial hash table has 11 locations:\n");		System.out.println("Initial dictionary should be empty; isEmpty() returns " + nameList.isEmpty());		// add 5 names - rehashing will not occur, since the load factor will be < 0.5			System.out.println("\n\nTesting add() - add 5 names:\n");		nameList.add(new Name("Tabatha"), "555-1234");		nameList.add(new Name("Toni"), "555-1235");		nameList.add(new Name("Tobbie"), "555-1236");		nameList.add(new Name("Tabbie"), "555-1237");		nameList.add(new Name("Tim"), "555-1238");				System.out.println("Dictionary should not be full; isFull() returns " + nameList.isFull() + "\n");		System.out.println("Dictionary contains " + nameList.getSize() + " (should be 5) items, as follows:\n");		display(nameList);			System.out.println("\nThe hash table is:\n");		nameList.display(); // display hash table [METHOD ADDED TO CLASS FOR TESTING PURPOSES]// --//    We now remove a name and add a name, so the table size remains the same. Our goal is to//    remove null from all table locations. Then we will test the method contains() in this situation.		System.out.println("\nRemove Tabatha, add Nancy:\n");		nameList.remove(new Name("Tabatha"));		nameList.add(new Name("Nancy"), "555-1239");				System.out.println("\nThe hash table is:\n");		nameList.display(); // display hash table [METHOD ADDED TO CLASS FOR TESTING PURPOSES]// --		System.out.println("\nRemove Toni, add Derek:\n");		nameList.remove(new Name("Toni"));		nameList.add(new Name("Derek"), "555-1230");		System.out.println("\nThe hash table is:\n");		nameList.display(); // display hash table [METHOD ADDED TO CLASS FOR TESTING PURPOSES]// --		System.out.println("\nRemove Tabbie, add Carole:\n");		nameList.remove(new Name("Tabbie"));		nameList.add(new Name("Carole"), "555-1230");		System.out.println("\nThe hash table is:\n");		nameList.display(); // display hash table [METHOD ADDED TO CLASS FOR TESTING PURPOSES]// --		System.out.println("\nRemove Tobbie, add Bette:\n");		nameList.remove(new Name("Tobbie"));		nameList.add(new Name("Bette"), "555-1230");		System.out.println("\nThe hash table is:\n");		nameList.display(); // display hash table [METHOD ADDED TO CLASS FOR TESTING PURPOSES]// --		System.out.println("\nRemove Tim, add Reiss:\n");		nameList.remove(new Name("Tim"));		nameList.add(new Name("Reiss"), "555-1230");		System.out.println("\nThe hash table is:\n");		nameList.display(); // display hash table [METHOD ADDED TO CLASS FOR TESTING PURPOSES]// --		System.out.println("\nRemove Tim, add Miguel:\n");		nameList.remove(new Name("Tim"));		nameList.add(new Name("Miguel"), "555-1230");		System.out.println("\nThe hash table is:\n");		nameList.display(); // display hash table [METHOD ADDED TO CLASS FOR TESTING PURPOSES]// --		System.out.println("\nRemove Bette, add Tom:\n");		nameList.remove(new Name("Bette"));		nameList.add(new Name("Tom"), "555-1230");		System.out.println("\nThe hash table is:\n");		nameList.display(); // display hash table [METHOD ADDED TO CLASS FOR TESTING PURPOSES]// --		System.out.println("\nLocate Reis, Carole, Nancy, and Zeke