public void addAfterCurrent(Object newEntry)                             throws NoSuchElementException{	if (hasCurrent())	{		Node newNode = new Node(newEntry);		newNode.next = currentNode.next;		currentNode.next = newNode;		length++;	}	else		throw new NoSuchElementException("addAfterCurrent(): " +	                                 "no current entry");} // end addAfterCurrent.