public void set(Object newEntry){	if (wasNextCalled && !wasAddCalled)		entry[currentIndex-1] = newEntry;	else if (wasPreviousCalled && !wasAddCalled)		entry[currentIndex] = newEntry;	else		throw new IllegalStateException("next() or previous() not " +		             "called for this invocation of set(), " +		             "OR remove() or add() was called after next() " +		             "or previous(), but before set().");} // end set