// 15.10public class Name extends ImmutableName{	public Name()	{	} // end default constructor	public Name(String firstName, String lastName)	{		super.setName(firstName, lastName);	} // end constructor	public void setName(String firstName, String lastName)	{		super.setName(firstName, lastName);	} // end setName	public void setFirst(String firstName)	{		super.setFirst(firstName);	} // end setFirst	public void setLast(String lastName)	{		super.setLast(lastName);	} // end setLast	public void giveLastNameTo(Name aName)	{		aName.setLast(this.getLast()); // 'this' is optional	} // end giveLastNameTo	public ImmutableName getImmutable()	{		return new ImmutableName(getFirst(), getLast());	} // end getImmutable} // end Name