// 2.8public class CollegeStudent extends Student{	private int    year;   // year of graduation	private String degree; // degree sought	public CollegeStudent()	{		super();     // must be first		year = 0;		degree = "";	} // end default constructor	public CollegeStudent(Name studentName, String studentId,	                      int graduationYear, String degreeSought)	{		super(studentName, studentId); // must be first		year = graduationYear;		degree = degreeSought;	} // end constructor	public void setStudent(Name studentName, String studentId,	                       int graduationYear, String degreeSought)	{		setName(studentName); // NOT fullName = studentName;		setId(studentId);     // NOT id = studentId;		// or setStudent(studentName, studentId); (see Segment 2.15)		year = graduationYear;		degree = degreeSought;	} // end setStudent	/* < The methods setYear, getYear, setDegree, getDegree go here. >	*/	public void setYear(int newYear)	{		year = newYear;	} // end setYear	public int getYear()	{		return year;	} // end getYear	public void setDegree(String newDegree)	{		degree = newDegree;	} // end setDegree	public String getDegree()	{		return degree;	} // end getDegree	public String toString()	{		return super.toString() + ", " + degree + ", " + year;	} // end toString} // end CollegeStudent